mapper.update:
UPDATE BOARD
SET BOARD_CATEGORY = #{board_category}, BOARD_TITLE =#{board_title}, BOARD_CONTENT =#{board_content}
WHERE BOARD_ID = #{board_id}

mapper.select:
SELECT * FROM REPLY
WHERE BOARD_ID = #{board_id}
ORDER BY COMMENT_REGDATE

SELECT count(*)
FROM REPLY
WHERE BOARD_ID = #{board_id}

mapper.delete:
DELETE FROM BOARD
WHERE BOARD_ID IN

	#{board_id}
